import React, {Component} from 'react';
import Client from '../contentful';

const PortfolioContext = React.createContext();

class PortfolioProvider extends Component {
	state = {
		items: [],
		currentItem: [],
		loading: true,
		currentPage: 1,
		previousPage: 1,
		nextPage: 2,
		numberPerPage: 10,
		totalOfItem: 0,
		totalOfPage: 0,
	}

	getData = async () => {
		try{
			let response = await Client.getEntries({
				content_type: "portfolio",
				order: "fields.dateUpdated"
			});

			let items = this.formatData(response.items);
			let currentItems = this.getItemsByPage(this.state.currentPage);

			this.setState({
				item: items,
				loading: false,
				TotalOfItems: items.length,
				currentItems: currentItems,
				totalOfPage: Math.ceil(items.length / this.state.numberPerPage)
			});

		} catch (error) {
			console.log(error);
		}
	}

	getItemsByPage = (page = 1) => {
		let startIndex = 0;
		let endIndex = this.state.numberPerPage;
		const items  = this.state.items;

		if(page > 1) {
			startIndex = (page - 1) * this.state.numberPerPage;
			endIndex = (this.state.numberPerPage * page) - 1;
		}
		
		let tempItems = items.slice(startIndex, endIndex);

		return tempItems;
	}

	handleLoadPage =  (page = 1) => {
		debugger;
		let currentPosts = this.getPostByPage(page);

		this.setState({
			currentPage: page,
			currentPosts: currentPosts,
			previousPage: (page > 1 ? page - 1 : page ),
			nextPage: (page < this.state.totalOfPost ? page + 1 : this.state.totalOfPost),
		});
 	}

	componentDidMount () {
		this.getData();
	}

	formatData =  (items) => {
        debugger;
		let tempItems = items.map(item => {

			let id = item.sys.id
			let image = item.fields.image.fields.file.url;
			let dateCreated = this.formatDate(item.fields.dateCreated);
			let dateUpdated = this.formatDate(item.fields.dateUpdated);

			let post = {...item.fields, id, image, dateCreated, dateUpdated};

			return post;
		});

		return tempItems;
	}
 
	render() {
		return(
			<PortfolioContext.Provider value={{ ...this.state, handleLoadPage: this.handleLoadPage, getItemsByPage: this.getItemsByPage }}>
				{this.props.children}
			</PortfolioContext.Provider>
		);
	}
}

const PortfolioConsumer = PortfolioContext.Consumer;

export function withPortfolioConsumer(Component) {
	return function consumerWrapper(props) {
		return <PortfolioConsumer>
			{value => <Component {...props} context={value} /> }
		</PortfolioConsumer>
	}
}

export {PortfolioProvider, PortfolioConsumer, PortfolioContext}
