import React from 'react';
import StickyNavigation from '../../components/StickyNavigation';
import SideHeader from '../../components/SideHeader';
import TopNavigation from '../../components/TopNavigation';
import BannerAd from '../../components/BannerAd';
import AboutUsWidget from '../../components/AboutUsWidget';
import FeaturedPostWidget from '../../components/FeaturedPostWidget';
import logo from '../../assets/img/logo.png';
import {useContext, useState} from 'react';
import {PostContext} from '../../context';
import AdImg from '../../assets/img/Webp.net-resizeimage-min.jpg';

const StandardLayout = (props) => {
	let context = useContext(PostContext);
	let {filterPostByKeyWord} = context;
	const [isOpenStickyMenu, setIsOpenStickyMenu] = useState(false);

	return (
		<div id="page-container" className="layout-masonry">
			<StickyNavigation isOpen={isOpenStickyMenu} onClickCloseBtn={setIsOpenStickyMenu(false)}/>
			<div id="page-content" className="page-content">
				<div id="sidebar" className="sidebar">
					<SideHeader logo={logo} onClickBarIcon={setIsOpenStickyMenu(true)} onSearch={filterPostByKeyWord} searchPage="search" />
				</div>
				<div id="content" className="content">
					<TopNavigation/>
					<main id="primary-content">
						<div className="main-wrapper">
							{props.children}
						</div>
					</main>
					<BannerAd name="Your Ad" img={AdImg} url="https://shopee.vn/" />
					<div id="secondary-content">
						<div className="widget-area">
							<aside className="widget">
								<AboutUsWidget/>
							</aside>
							<aside className="widget">
								<FeaturedPostWidget/>
							</aside>
							
						</div>
					</div>
				</div>
			</div>
		</div>
	);
}

export default StandardLayout;
